"use strict";
function getRandomColor() {
    var rgb = [];
    for (var i = 0; i < 3; ++i) {
        var color = Math.floor(Math.random() * 256).toString(16);
        color = color.length == 1 ? '0' + color : color;
        rgb.push(color);
    }
    return '#' + rgb.join('');
}
Page({
    onReady: function () {
        this.videoContext = wx.createVideoContext('myVideo');
    },
    inputValue: '',
    videoContext: {},
    data: {
        url: '',
        courseName: '',
        danmuList: [
            {
                text: '第 1s 出现的弹幕',
                color: '#ff0000',
                time: 1
            },
            {
                text: '第 3s 出现的弹幕',
                color: '#ff00ff',
                time: 3
            }
        ]
    },
    onLoad: function () {
        try {
            var coursename = wx.getStorageSync('name');
            var ur = wx.getStorageSync('url');
            this.setData({
                courseName: coursename,
                url: ur
            });
            wx.setStorageSync('lastTime', 0);
        }
        catch (e) {
        }
    },
    bindInputBlur: function (e) {
        this.inputValue = e.detail.value;
    },
    bindSendDanmu: function () {
        this.videoContext.sendDanmu({
            text: this.inputValue,
            color: getRandomColor()
        });
    },
    timeUpdate: function (e) {
        var lastTime = wx.getStorageSync('lastTime');
        if (Math.abs(e.detail.currentTime - lastTime) > 2) {
            this.videoContext.seek(lastTime);
            return;
        }
        wx.setStorageSync('lastTime', e.detail.currentTime);
    },
    finish: function () {
        console.log('finish');
    }
});
